sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if(isLoading || newValue==oldValue||newValue==='')
		{
			return;
		}

   //Type appropriate comment here, and begin script below
	var getDepartment=new GlideAjax("GetDepartment");
	getDepartment.addParam('sysparm_name','getDept');
	getDepartment.addParam('sysparm_userID',g_form.getValue('u_requested_for'));
	getDepartment.getXML(populateDepartment);

	function populateDepartment()
	{
	   // Extract the email address from the response, clear any value from the email field, 
		// set new value in the email field
		var deptFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
		g_form.clearValue('u_department');
		g_form.setValue('u_department',deptFromScriptInclude);	
	}


}
